// Get current soil moisture and rain status
var soilMoisture = parseFloat(msg.payload);
var rainStatus = flow.get('main_rain_status') || false;
var soilThreshold = flow.get('main_soil_threshold') || 40;
var systemMode = flow.get('system_mode') || 'auto';
var manualOverride = flow.get('main_manual_override') || false;

// Store current soil moisture
flow.set('main_soil_moisture', soilMoisture);

var shouldWater = false;
var reason = '';

if (systemMode === 'emergency') {
    shouldWater = false;
    reason = 'Emergency stop activated';
} else if (systemMode === 'manual' || manualOverride) {
    shouldWater = flow.get('main_manual_valve_state') || false;
    reason = 'Manual override active';
} else if (systemMode === 'auto') {
    if (rainStatus) {
        shouldWater = false;
        reason = 'Rain detected - automatic watering disabled';
    } else if (soilMoisture < soilThreshold) {
        shouldWater = true;
        reason = `Soil moisture (${soilMoisture}%) below threshold (${soilThreshold}%)`;
    } else {
        shouldWater = false;
        reason = `Soil moisture (${soilMoisture}%) above threshold (${soilThreshold}%)`;
    }
}

// Store irrigation decision
flow.set('main_irrigation_active', shouldWater);

// Create status message
var statusMsg = {
    payload: shouldWater,
    topic: 'agrosense/main/valve_status',
    timestamp: new Date().toISOString(),
    soil_moisture: soilMoisture,
    threshold: soilThreshold,
    rain_status: rainStatus,
    reason: reason,
    mode: systemMode
};

return statusMsg;

// Get current soil moisture for remote field
var soilMoisture = parseFloat(msg.payload);
var soilThreshold = flow.get('remote_soil_threshold') || 40;
var systemMode = flow.get('system_mode') || 'auto';
var manualOverride = flow.get('remote_manual_override') || false;

// Store current soil moisture
flow.set('remote_soil_moisture', soilMoisture);

var shouldWater = false;
var reason = '';

if (systemMode === 'emergency') {
    shouldWater = false;
    reason = 'Emergency stop activated';
} else if (systemMode === 'manual' || manualOverride) {
    shouldWater = flow.get('remote_manual_valve_state') || false;
    reason = 'Manual override active';
} else if (systemMode === 'auto') {
    if (soilMoisture < soilThreshold) {
        shouldWater = true;
        reason = `Remote soil moisture (${soilMoisture}%) below threshold (${soilThreshold}%)`;
    } else {
        shouldWater = false;
        reason = `Remote soil moisture (${soilMoisture}%) above threshold (${soilThreshold}%)`;
    }
}

// Store irrigation decision
flow.set('remote_irrigation_active', shouldWater);

// Create status message
var statusMsg = {
    payload: shouldWater,
    topic: 'agrosense/remote/valve_status',
    timestamp: new Date().toISOString(),
    soil_moisture: soilMoisture,
    threshold: soilThreshold,
    reason: reason,
    mode: systemMode
};

return statusMsg;

// Aggregate system status information
var mainTemp = parseFloat(msg.payload);
var timestamp = new Date().toLocaleString();

// Get stored values
var mainIrrigationActive = flow.get('main_irrigation_active') || false;
var remoteIrrigationActive = flow.get('remote_irrigation_active') || false;
var rainStatus = flow.get('main_rain_status') || false;
var activeFields = 2; // Main + Remote

// Determine overall irrigation status
var irrigationActive = mainIrrigationActive || remoteIrrigationActive;

// Determine weather status
var weather = rainStatus ? 'rain' : 'clear';

// Create comprehensive status message
var statusMsg = {
    payload: {
        timestamp: timestamp,
        connection_status: 'Online',
        active_fields: activeFields + ' Fields',
        irrigation_active: irrigationActive,
        weather: weather,
        main_temp: mainTemp
    },
    timestamp: timestamp,
    connection_status: 'Online',
    active_fields: activeFields + ' Fields',
    irrigation_active: irrigationActive,
    weather: weather
};

return statusMsg;

// Create CSV export functionality
var exportData = {
    timestamp: new Date().toISOString(),
    main_temperature: flow.get('main_temperature') || 0,
    main_humidity: flow.get('main_humidity') || 0,
    main_soil_moisture: flow.get('main_soil_moisture') || 0,
    main_light_level: flow.get('main_light_level') || 0,
    remote_temperature: flow.get('remote_temperature') || 0,
    remote_humidity: flow.get('remote_humidity') || 0,
    remote_soil_moisture: flow.get('remote_soil_moisture') || 0,
    main_irrigation_status: flow.get('main_irrigation_active') || false,
    remote_irrigation_status: flow.get('remote_irrigation_active') || false
};

// Format as CSV
var csvHeader = 'Timestamp,Main_Temp,Main_Humidity,Main_Soil,Main_Light,Remote_Temp,Remote_Humidity,Remote_Soil,Main_Irrigation,Remote_Irrigation\n';
var csvRow = `${exportData.timestamp},${exportData.main_temperature},${exportData.main_humidity},${exportData.main_soil_moisture},${exportData.main_light_level},${exportData.remote_temperature},${exportData.remote_humidity},${exportData.remote_soil_moisture},${exportData.main_irrigation_status},${exportData.remote_irrigation_status}\n`;

msg.payload = csvHeader + csvRow;
msg.filename = `agrosense_data_${new Date().toISOString().split('T')[0]}.csv`;

return msg;
